package preCourse.ch01;

public class Claculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x;							// 변수 x 선언
		x = 1;							// 변수 x 에 1 저장
		int y = 2;						// 변수 y 선언하고 2 저장
		int result = x + y;				// 변수 result 선언하고 x 와 y 에 저장한 값 저장
		System.out.println(result);		// 콘솔에 출력하는 method 호출
	}

}
